import React, { useState } from 'react';

const FieldWithDots = () => {
  const [dots, setDots] = useState([]);

  // Функция для генерации случайной позиции
  const getRandomPosition = () => ({
    top: Math.random() * 90 + '%',  // расположение по вертикали
    left: Math.random() * 90 + '%', // расположение по горизонтали
  });

  const handleClick = () => {
    setDots([...dots, getRandomPosition()]);
  };

  return (
    <div
      style={{
        position: 'relative',
        width: '100vw',
        height: '100vh',
        backgroundColor: '#f0f0f0',
      }}
      onClick={handleClick}
    >
      {dots.map((dot, index) => (
        <div
          key={index}
          style={{
            position: 'absolute',
            top: dot.top,
            left: dot.left,
            width: '10px',
            height: '10px',
            backgroundColor: 'red',
            borderRadius: '50%',
          }}
        />
      ))}
      <p style={{ position: 'absolute', bottom: '10px', left: '50%', transform: 'translateX(-50%)' }}>
        Нажмите на поле, чтобы добавить точку
      </p>
    </div>
  );
};

export default FieldWithDots;
