import React, { useState, useEffect } from "react";
import "./Carousel.css";

const Projects = () => {
  const [index, setIndex] = useState(0); // Индекс текущего проекта
  const projects = [
    { 
      image: "https://via.placeholder.com/800x400?text=Проект+1", 
      link: "https://example.com/project1" 
    },
    { 
      image: "https://via.placeholder.com/800x400?text=Проект+2", 
      link: "https://example.com/project2" 
    },
    { 
      image: "https://via.placeholder.com/800x400?text=Проект+3", 
      link: "https://example.com/project3" 
    },
    { 
      image: "https://via.placeholder.com/800x400?text=Проект+4", 
      link: "https://example.com/project4" 
    },
    { 
      image: "https://via.placeholder.com/800x400?text=Проект+5", 
      link: "https://example.com/project5" 
    },
  ];

  useEffect(() => {
    const interval = setInterval(() => {
      setIndex((prevIndex) => (prevIndex + 1) % projects.length);
    }, 3000); // Автопрокрутка каждые 3 секунды
    return () => clearInterval(interval); // Очищаем таймер при размонтировании
  }, [projects.length]);

  return (
    <div className="carousel-container">
      <div
        className="carousel"
        style={{ transform: `translateX(-${index * 100}%)` }}
      >
        {projects.map((project, i) => (
          <a 
            key={i} 
            href={project.link} 
            className="project"
            target="_blank" 
            rel="noopener noreferrer"
          >
            <img src={project.image} alt={`Проект ${i + 1}`} />
          </a>
        ))}
      </div>
    </div>
  );
};

export default Projects;
