import React, { useState } from 'react';

const PointGame = () => {
  const [points, setPoints] = useState([{ x: 100, y: 100 }]);

  // Функция для генерации случайных координат в пределах поля
  const getRandomPosition = () => {
    const x = Math.floor(Math.random() * 400); // Ширина поля (400px)
    const y = Math.floor(Math.random() * 400); // Высота поля (400px)
    return { x, y };
  };

  // Обработчик клика по точке
  const handleClick = (e) => {
    const { offsetX, offsetY } = e.nativeEvent;
    
    // Проверяем, попали ли в одну из существующих точек
    const tolerance = 10; // Радиус точки, в который можно кликнуть
    for (let i = 0; i < points.length; i++) {
      const point = points[i];
      if (
        Math.abs(offsetX - point.x) < tolerance &&
        Math.abs(offsetY - point.y) < tolerance
      ) {
        // Добавляем новую точку в случайное место
        setPoints((prevPoints) => [...prevPoints, getRandomPosition()]);
        break; // Выход из цикла, если точка найдена и новая точка добавлена
      }
    }
  };

  return (
    <div
      style={{
        width: '400px',
        height: '400px',
        border: '1px solid black',
        position: 'relative',
      }}
      onClick={handleClick}
    >
      {points.map((point, index) => (
        <div
          key={index}
          style={{
            position: 'absolute',
            top: point.y,
            left: point.x,
            width: '10px',
            height: '10px',
            borderRadius: '50%',
            backgroundColor: 'red',
            cursor: 'pointer',
          }}
        ></div>
      ))}
    </div>
  );
};

export default PointGame;
