import React, { useState } from 'react';

const PointGame = () => {
  const [points, setPoints] = useState([{ x: 100, y: 100 }]);

  // Функция для генерации случайных координат в пределах поля
  const getRandomPosition = () => {
    const x = Math.floor(Math.random() * 400); // Ширина поля (400px)
    const y = Math.floor(Math.random() * 400); // Высота поля (400px)
    return { x, y };
  };

  // Обработчик клика на точку
  const handleClick = (e) => {
    const { offsetX, offsetY } = e.nativeEvent;
    // Проверяем, что клик был внутри первой точки
    if (
      Math.abs(offsetX - points[points.length - 1].x) < 10 &&
      Math.abs(offsetY - points[points.length - 1].y) < 10
    ) {
      setPoints([...points, getRandomPosition()]);
    }
  };

  return (
    <div
      style={{
        width: '400px',
        height: '400px',
        border: '1px solid black',
        position: 'relative',
      }}
      onClick={handleClick}
    >
      {points.map((point, index) => (
        <div
          key={index}
          style={{
            position: 'absolute',
            top: point.y,
            left: point.x,
            width: '10px',
            height: '10px',
            borderRadius: '50%',
            backgroundColor: 'red',
          }}
        ></div>
      ))}
    </div>
  );
};

export default PointGame;
